#function to encrypt the password using the cezar cipher
def encrepter_cezar(password, login, app):
    
    #initialize the encrypted password
    encrypted_password = ''

    #using the length of the password as the key
    ceazer_key = len(password)

    #encrypt the password using the key
    for char in password:
        if char in char_to_index:
            idx = char_to_index[char]
            new_idx = (idx + ceazer_key) % len(vet_cezar)
            encrypted_password += index_to_char[new_idx]
        #if the character is not in the vector, it keeps the original character
        else:
            encrypted_password += char  

    #return the encrypted password
    return encrypted_password

#function to decrypt the password using the cezar cipher
def decripter_cezar(encrypted_password):
    
    #initialize the decrypted password
    decrypted_password = ''

    #using the length of the encrypted password as the key
    ceazer_key = len(encrypted_password)

    #decrypt the password using the key
    for char in encrypted_password:
        if char in char_to_index:
            idx = char_to_index[char]
            new_idx = (idx - ceazer_key) % len(vet_cezar)
            decrypted_password += index_to_char[new_idx]
        #if the character is not in the vector, it keeps the original character
        else:
            decrypted_password += char

    #return the decrypted password
    return decrypted_password

#function to encrypt the password using the vinegere cipher
def encrypted_vinegere(password, key):
    
    #declare the global variables
    global vet_vinegere
    global matriz_vinegere
    
    #initialize the encrypted password
    password_vinegere = ''
    
    #get the length of the key
    key_length = len(key)
    #get the index of the key characters
    key_indices = [vet_vinegere.index(k) for k in key]

    #encrypt the password using the key
    for i, char in enumerate(password):
        password_index = vet_vinegere.index(char)
        key_index = key_indices[i % key_length]
        password_vinegere += matriz_vinegere[password_index][key_index]
    
    #return the encrypted password
    return password_vinegere

#function to decrypt the password using the vinegere cipher
def decrypted_vinegere(password_vinegere, key):
    
    #declare the global variables
    global vet_vinegere
    global matriz_vinegere
    
    #initialize the decrypted password
    password_decifrado = ''
    
    #get the length of the key
    key_length = len(key)
    #get the index of the key characters
    key_indices = [vet_vinegere.index(k) for k in key]

    #decrypt the password using the key
    for i, char in enumerate(password_vinegere):
        key_index = key_indices[i % key_length]
        row = matriz_vinegere[key_index]
        password_index = row.index(char)
        password_decifrado += vet_vinegere[password_index]
        
    #return the decrypted password
    return password_decifrado
    
